import {
    ADD_CONTACT ,
    DELETE_CONTACT ,
    SET_CURRENT ,
    CLEAR_CURRENT ,
    UPDATE_CONTACT ,
    FILTER_CONTACT ,
    CLEAR_FILTER ,
    SET_ALERT ,
    REMOVE_ALERT
} from '../types';

export default (state, action) => {
    switch (action.type) {
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [...state.contacts, action.payload]
            }

        case DELETE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.filter((i) => i.id !== action.payload)
            }

        case SET:
            return {
                ...state,
                contacts: state.contacts.filter((i) => i.id !== action.payload)
            }    

        default:
           return state;
    }
}
