import React, {useState, useContext} from 'react';
import AuthContext from '../../context/auth/authContext'

const Login = () => {
    const [user, setUser] = useState({
        email : '',
        password : '',
        cpassword : '',
    })

    const { email, password } = user;

    const onChange = (e) => {
        setUser({...user, [e.target.name] : e.target.value})
    }

    const onSubmit = (e) => {
        e.preventDefault();
        console.log('Logged In');
    }

    return (
        <div className="form-container">
            <h1>Account <span className="text-primary">Log In</span></h1>
            <form action="" onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor="email"> Email </label>
                    <input type="text" email='email' value={email} onChange={onChange}/>
                </div>
                

                <div className="form-group">
                    <label htmlFor="password"> Password </label>
                    <input type="text" password='password' value={password} onChange={onChange}/>
                </div>
                
                <input type="submit" value="Log In" className="btn btn-block"/>
            </form>
        </div>
    )
}

export default Login
