import {
    SET_ALERT,
    REMOVE_ALERT
} from '../types';

export default (state,action) => {
    switch (action.type) {
        case SET_ALERT: 
            return [...state, action]
                    
        case REMOVE_ALERT:
            
                
        default:
            return state;
    }
}
