import {
    REGISTER_SUCCESS,
    REGISTER_FAIL ,
    LOGIN_SUCCESS ,
    LOGIN_FAIL ,
    USER_LOADED ,
    LOGOUT ,
    AUTH_ERROR ,
    CLEAR_ERROR 
} from '../types';

export default (state, action) => {
    switch (action.type) {
        case USER_LOADED : 
            return {
                ...state,

            };


        case REGISTER_SUCCESS :
            localStorage.setItem('token', action.payload.token);
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true,
                loading: false
            };


        case REGISTER_FAIL : 
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                isAuthenticated: false,
                loading: false,
                user: null,
                error: action.payload
            };
        

        // case LOGIN_SUCCESS : 
        //     return {

        //     };
        

        // case LOGIN_FAIL : 
        //     return {

        //     };
        

        

        // case LOGOUT : 
        //     return {

        //     };
        

        // case AUTH_ERROR : 
        //     return {

        //     };
        

        case CLEAR_ERROR : 
            return {
                ...state,
                error: null
            };


        default:
            return state;
    }
}
