import React, { useState, useContext, useEffect} from 'react';
import ContactContext from '../../context/contact/contactContext'


const ContactForm = () => {
    const contactContext = useContext(ContactContext);
    const {addContact, current, clearCurrent} = contactContext;

    const [contact, setContact] = useState({
        name : '',
        email : '',
        phone : '',
        type : 'personal'
    });

    const {name, email, phone, type} = contact;

    const onChange = (e) => setContact({ ...contact , [e.target.name] : e.target.value});
    //Copy whatever the current state is & update the changes

    const onSubmit = (e) => {
        e.preventDefault();
        addContact(contact);
        setContact({
            name : '',
            email : '',
            phone : '',
            type : 'personal' 
        })
    }

    useEffect(() => {
       if(current){
            setContact(current);
       }else{
            setContact({
                name : '',
                email : '',
                phone : '',
                type : 'personal' 
            })
       }
    }, [contactContext, current])

    return (
        <form onSubmit = {onSubmit}>
                <h2 className="text-primary">{current === null ? 'Add Contact' : 'Update Contact'}</h2>
                <input type="text" name="name" placeholder="Enter Name" value={name} onChange={onChange}/>
                <input type="email" name="email" placeholder="Enter e-mail" value={email} onChange={onChange}/>
                <input type="text" name="phone" placeholder="Enter Phone number" value={phone} onChange={onChange}/>
                <h4>Contact Type</h4>
                <input type="radio" name="type" value='personal' checked={type === 'personal'} onChange={onChange}/>Personal&ensp;&ensp;
                <input type="radio" name="type" value='professional' checked={type === 'professional'} onChange={onChange}/>Professional
                <div>
                    <input type="submit" value="Save Contact" className="btn btn-primary btn-block"/>
                </div>
        </form>
        
    )
}

export default ContactForm
