import {
    ADD_CONTACT ,
    DELETE_CONTACT ,
    SET_CURRENT ,
    CLEAR_CURRENT ,
    UPDATE_CONTACT ,
    FILTER_CONTACT ,
    CLEAR_FILTER ,
    SET_ALERT ,
    REMOVE_ALERT
} from '../types';

export default (state, action) => {
    switch (action.type) {
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [...state.contacts, action.payload]
            };

        case DELETE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.filter((i) => i.id !== action.payload)
            };

        case SET_CURRENT:
            return {
                ...state,
                current: action.payload
            };  

        case CLEAR_CURRENT:
            return {
                ...state,
                current: null
            };
        
        case UPDATE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.map(i => i.id === action.payload.id ? action.payload : i)
            };

        case FILTER_CONTACT:
            return {
                ...state,
                filter: state.contacts.name.in
            };        
       
        default:
           return state;
    }
}
