import {
    ADD_CONTACT ,
    DELETE_CONTACT ,
    SET_CURRENT ,
    CLEAR_CURRENT ,
    UPDATE_CONTACT ,
    CONTACT_ERROR,
    FILTER_CONTACT ,
    CLEAR_FILTER ,
    SET_ALERT ,
    REMOVE_ALERT
} from '../types';

export default (state, action) => {
    switch (action.type) {
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [...state.contacts, action.payload]
            };

        case DELETE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.filter((i) => i.id !== action.payload)
            };

        case SET_CURRENT:
            return {
                ...state,
                current: action.payload
            };  

        case CLEAR_CURRENT:
            return {
                ...state,
                current: null
            };
        
        case UPDATE_CONTACT:
            return {
                ...state,
                contacts: state.contacts.map(i => i.id === action.payload.id ? action.payload : i)
            };

        case FILTER_CONTACT:
            return {
                ...state,
                filtered: state.contacts.filter((i) => {
                    if(i.name.toLowerCase().includes(action.payload) || 
                       i.email.toLowerCase().includes(action.payload)){
                        return i;
                    }
                })
            };        
        
        case CLEAR_FILTER:
            return {
                ...state,
                filtered: null
            }; 
            
        case DELETE_CONTACT:
                return {
                    ...state,
                    contacts: state.contacts.filter((i) => i.id !== action.payload)
                };

        default:
           return state;
    }
}
